mapper.select:
		select a.id commodityId,a.commodityCategory1,a.commodityCategory2,a.commodityCategory3,a.commodityName,a.brand,a.price,
		a.quantity,a.uom,a.color,a.size,a.saleState,a.goodFlg,a.hotFlg,a.choiceFlg,a.appreciateFlg,a.picture1,a.picture2,a.picture3,
		a.picture4,a.picture5,b.id commodityShowId,b.commodityIntroduction,b.packingSpecification,b.instructions,b.customerService,
		b.showPagePath,b.showPageSource,a.memberId,c.memberName,c.logoPath,c.reputation,c.guarantee,c.highQuality,c.sincerity,returnGoods
		from commodity a
		inner join commodity_show b on a.id = b.commodityId
		left join member c on a.memberId = c.id
		where 1=1
		
			and a.id = #{commodity.id}
		
		
			and a.memberId = #{commodity.memberId}
		
		
			and a.commodityCategory1 = #{commodity.commodityCategory1}
		
		
			and a.commodityCategory2 = #{commodity.commodityCategory2}
		
		
			and a.commodityCategory3 = #{commodity.commodityCategory3}
		
		
			and a.commodityName like '%${commodity.commodityName}%'
        
		
			and a.brand = #{commodity.brand}
		
		
			and a.color = #{commodity.color}
		
		
			and a.size = #{commodity.size}
		
		
			and a.price >= #{commodity.priceMin}
        
		
			and a.price <= #{commodity.priceMax}
        
		
			and a.saleState = #{commodity.saleState}
		
		
			and a.goodFlg = #{commodity.goodFlg}
		
		
			and a.hotFlg = #{commodity.hotFlg}
		
		
			and a.choiceFlg = #{commodity.choiceFlg}
		
		
			and a.appreciateFlg = #{commodity.appreciateFlg}
		
		
			and a.id = #{commodityId}
		
		
			and a.memberId = #{memberId}
		
		
			and a.commodityCategory1 = #{commodityCategory1}
        
		
			and a.commodityCategory2 = #{commodityCategory2}
        
		
			and a.commodityCategory3 = #{commodityCategory3}
        
		
			and a.commodityName like '%${commodityName}%'
        
		
			and a.brand = #{brand}
        
		
			and a.color = #{color}
        
		
			and a.size = #{size}
        
		
			and a.price >= #{priceMin}
        
		
			and a.price <= #{priceMax}
        
		
			and a.saleState = #{saleState}
		
		
			and a.goodFlg = #{goodFlg}
		
		
			and a.hotFlg = #{hotFlg}
		
		
			and a.choiceFlg = #{choiceFlg}
		
		
			and a.appreciateFlg = #{appreciateFlg}
		
        order by  
        
			a.hotFlg asc,
        
        
			a.hotFlg desc,
        
        
			a.price asc,
        
        
			a.price desc,
        
        
			a.createTime asc,
        
        
			a.createTime desc,
        
        
			a.hotFlg asc,
        
        
			a.hotFlg desc,
        
        
			a.price asc,
        
        
			a.price desc,
        
        
			a.createTime asc,
        
        
			a.createTime desc,
        
        c.reputation desc, a.id desc

		select * from commoditycategorya
		where 1=1
		
			and id = #{id}
		
		
			and categoryName = #{categoryName}
		
		
			and status = #{status}
		
        order by  
        createTime

		select * from commoditycategoryb
		where 1=1
		
			and id = #{id}
		
		
			and categoryAId = #{categoryAId}
		
		
			and categoryName = #{categoryName}
		
		
			and status = #{status}
		
        order by  
        createTime

		select * from commoditycategoryc
		where 1=1
		
			and id = #{id}
		
		
			and categoryBId = #{categoryBId}
		
		
			and categoryName = #{categoryName}
		
		
			and status = #{status}
		
        order by  
        createTime
